import { createSelector, createFeatureSelector } from '@ngrx/store';
export const selectChatParticipantState = createFeatureSelector('chatParticipantState');
export const selectChatParticipants = () => createSelector(selectChatParticipantState, state => state.chatParticipants);
export const selectChatParticipant = id => createSelector(selectChatParticipantState, state => state.chatParticipants.find(d => d.id === id));
