import { createSelector, createFeatureSelector } from '@ngrx/store';
export const selectVideoPlayerState = createFeatureSelector('videoPlayerState');
export const selectVideoPlayers = () => createSelector(selectVideoPlayerState, state => state.videoPlayers);
export const selectVideoPlayer = id => createSelector(selectVideoPlayerState, state => state.videoPlayers.find(d => d.id === id));
